import { Button, useMediaQuery } from '@mui/material';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import heroImage from './hero.svg';

function Hero() {
  const isSmallScreen = useMediaQuery('(max-width:800px)'); // Define your breakpoint here

  return (
    <Container
      style={{
        background: '#F2E7D6',
        position: 'relative',
        padding: '1%',
        marginRight: '1%',
        minHeight: '800px',
        paddingBottom: "10%",
        width: "100%"
      }}
      maxWidth="xl"
    >
      <Grid
        container
        // spacing={isSmallScreen ? 2 : 10}
        alignContent="flex-start"
        alignItems="center"
        justifyContent="center"
        sx={{ m: 2, width: "100%", spacingRight: {
          xs: "20%",
          sm: "15%",
          ml: "12%", 
          lg: "10%",
          xl: '3%'

          } }}
      >
        <Grid item xs={12} sm={6}>
          <Box p={0}>
            <img
              src={heroImage}
              height="auto"
              width="100%"
              alt="Hero Image"
            />
          </Box>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Box
            style = {{width:"100%",  alignItems: {
              xs: "center",
              sm: "center",
              ml: "center", 
              lg: "flex-start",
              xl: 'flex-start'
              }}
            }
            p={0}
            display="flex"
            flexDirection="column"
            // alignItems={isSmallScreen ? 'center' : 'flex-start'}
          >
            <h1
              style={{
                fontFamily: 'yeseva one',
                fontSize: isSmallScreen ? 32 : 80,
                textAlign:  {
                  xs: "center",
                  sm: "center",
                  ml: "center", 
                  lg: "left",
                  xl: 'left'
                },
                // textAlign: isSmallScreen ? 'center' : 'left',
                width: isSmallScreen ? '50%' : '70%'
              }}
            >
              Ideas that build exceptional growth
            </h1>
            <p
              style={{
                fontFamily: 'josefin sans',
                fontSize: isSmallScreen ? 18 : 25,
                textAlign:  {
                  xs: "center",
                  sm: "center",
                  ml: "center", 
                  lg: "left",
                  xl: 'left'
                },
                width: isSmallScreen ? '50%' : '70%'
              }}
            >
              Thereâ€™s a smarter way to do business. A way that includes
              next-level services, a determined, relentless, and passionate
              consultant, and a unique perspective to help startups, founders,
              and visionaries get to the next level.
            </p>
            <Box
              p={0}
              style={{
                textAlign: isSmallScreen ? 'center' : 'left',
                marginTop: '20px',
              }}
            >
              <Button
                style={{
                  fontFamily: 'montserrat',
                  color: 'white',
                  backgroundColor: '#154D43',
                  borderRadius: 10,
                  display: 'inline-block',
                  alignItems: "flex-start"
                }}
                alignItems="left"
              >
                Schedule a Meeting
              </Button>
            </Box>
          </Box>
        </Grid>
      </Grid>
      <Box
        alignItems="center"
        justifyContent="center"
        style={{
          backgroundColor: '#CCA06F',
          borderRadius: 10,
          position: 'absolute',
          bottom: '-15%',
          left: '50%',
          transform: 'translateX(-50%)',
          // padding: '2%',
          borderRadius: '30px',
          zIndex: 3,
          width: {
            xs: "7%",
            sm: "10%",
            ml: "20%", 
            lg: "30%",
            xl: '40%'
          },
          paddingTop: {
            xs: "25%",
            sm: "15%",
            ml: "12%", 
            lg: "15%",
            xl: '30%'
            }
        }}
      >
        <p
          style={{
            width: '100%',
            color: 'white',
            fontFamily: 'josefin sans',
            fontSize: isSmallScreen ? 20 : 35,
            lineHeight: isSmallScreen ? '25px' : '35px',
            textAlign: 'center',
            position: "relative"
          }}
        >
          An unwavering commitment to helping entrepreneurs, leaders, and those
          with big ideas achieve new heights and success.
        </p>
      </Box>
    </Container>
  );
}

export default Hero;
