import ButtonAppBar from './navbar';
import Testimonials from './Testimonials';
import Hero from './Hero';
import Whoami from './Whoami';
import Services from './Services';
import Price from './Price';
import Processcomponent from './Processcomponent';
import Footer from './footer';
import { Container, useMediaQuery } from '@mui/material';

function App() {
  const isSmallScreen = useMediaQuery('(max-width:600px)'); // Define your breakpoint here

  return (
    <>
      <Container
        maxWidth="xl"
        sx={{
          display: 'flex',
          flexDirection: 'column', // Stack components vertically on small screens
          alignItems: isSmallScreen ? 'center' : 'stretch', // Center content on small screens
        }}
        style={{ width: '100%', position: 'absolute', minHeight: '100vh',  display: 'flex' }}
      >
        <ButtonAppBar />
        <Hero />
        <Whoami />
        {/* <Processcomponent></Processcomponent> */}
        <Services />
        <Price />
        <Testimonials />
        <Footer />
      </Container>
    </>
  );
}

export default App;
