import { Button, useMediaQuery } from '@mui/material';
import Grid from '@mui/material/Grid';
import Container from '@mui/material/Container';
import Box from '@mui/material/Box';
import heroImage from './hero.svg';
import { styled } from '@mui/system';

// Define a styled component to handle dynamic text width
const ResponsiveText = styled('p')(({ theme, isSmallScreen }) => ({
  fontFamily: 'josefin sans',
  fontSize: isSmallScreen ? (18) :(25),
  textAlign: isSmallScreen ? 'center' : 'left',
  maxWidth: isSmallScreen ? '100%' : '70%', // Adjusted maxWidth for responsiveness
}));

function Hero() {
  const isSmallScreen = useMediaQuery('(max-width:600px)'); // Define your breakpoint here

  return (
    <Container
      style={{
        background: '#F2E7D6',
        position: 'relative',
        padding: '1%',
        marginRight: '1%',
        minHeight: '800px',
        paddingBottom: '10%',
        width: '100%',
      }}
      maxWidth="xl"
    >
      <Grid
        container
        spacing={isSmallScreen ? 2 : 10}
        alignContent="flex-start"
        alignItems="center"
        justifyContent="center"
        sx={{ m: 2 }}
        style={{ width: '100%' }}
      >
        <Grid item xs={12} sm={6}>
          <Box p={0}>
            <img
              src={heroImage}
              height="auto"
              width="100%"
              alt="Hero Image"
            />
          </Box>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Box
            p={0}
            display="flex"
            flexDirection="column"
            alignItems={isSmallScreen ? 'center' : 'flex-start'}
          >
            <h1
              style={{
                fontFamily: 'yeseva one',
                fontSize: isSmallScreen ? 32 : 80,
                textAlign: isSmallScreen ? 'center' : 'left',
                width: '100%',
              }}
            >
              Ideas that build exceptional growth
            </h1>
            <ResponsiveText isSmallScreen={isSmallScreen}>
              Thereâ€™s a smarter way to do business. A way that includes
              next-level services, a determined, relentless, and passionate
              consultant, and a unique perspective to help startups, founders,
              and visionaries get to the next level.
            </ResponsiveText>
            <Box
              p={0}
              style={{
                textAlign: isSmallScreen ? 'center' : 'left',
                marginTop: '20px',
              }}
            >
              <Button
                style={{
                  fontFamily: 'montserrat',
                  color: 'white',
                  backgroundColor: '#154D43',
                  borderRadius: 10,
                  display: 'inline-block',
                }}
              >
                Schedule a Meeting
              </Button>
            </Box>
          </Box>
        </Grid>
      </Grid>
      <Box
        alignItems="center"
        justifyContent="center"
        style={{
          backgroundColor: '#CCA06F',
          borderRadius: 10,
          position: 'absolute',
          bottom: '-15%',
          left: '50%',
          transform: 'translateX(-50%)',
          width: '40%',
          padding: '2%',
          borderRadius: '30px',
          zIndex: 3,
        }}
      >
        <p
          style={{
            marginTop: '7%',
            width: '100%',
            color: 'white',
            fontFamily: 'josefin sans',
            fontSize: isSmallScreen ? 20 : 35,
            lineHeight: isSmallScreen ? '25px' : '35px',
            textAlign: 'center',
            position: 'relative',
          }}
        >
          An unwavering commitment to helping entrepreneurs, leaders, and those
          with big ideas achieve new heights and success.
        </p>
      </Box>
    </Container>
  );
}

export default Hero;
